pragma solidity ^0.8.0;

import "../token/ERC721/ERC721.sol";
import "../utils/Pausable.sol";
import "../access/Ownable.sol";
import "../utils/Counters.sol";
import "../utils/libraries/EnumerableSet.sol";
import "../utils/SafeTransfer.sol";

contract BoarcDrop is ERC721, SafeTransfer, Pausable, Ownable {
	using EnumerableSet for EnumerableSet.AddressSet;
	using Counters for Counters.Counter;

	EnumerableSet.AddressSet whitelist;
	Counters.Counter private tokenIdCounter;
	uint256 public constant maxSupply = 99;

	uint256 public royaltiesPercent = 1000;
	uint256 public feeMint;
	address public feeRecipient;
	string public baseURI;

	mapping(uint256 => address) public creator;

	event Minted(address owner, uint256 tokenId, uint256 time);
	event DropPaused(uint256 timestamp);
	event DropUnpaused(uint256 timestamp);
	event BaseURIChanged(string newBaseURI);
	event WithdrawToken(address token, uint256 amount);

	constructor(
		string memory _uri,
		uint256 _feeMint,
		address _feeRecipient
	) ERC721("Art Of Bamboo", "BOARC") {
		require(_feeRecipient != address(0), "BD: zero address");
		baseURI = _uri;
		feeMint = _feeMint;
		feeRecipient = _feeRecipient;
	}

	function tokenURI(uint256 tokenId) public view override returns (string memory) {
		return string(abi.encodePacked(super.tokenURI(tokenId), ".json"));
	}

	function totalSupply() public view returns (uint256) {
		return tokenIdCounter.current();
	}

	function whiteList() public view returns (address[] memory _whitelist) {
		uint256 lenght = whitelist.length();
		_whitelist = new address[](lenght);
		for (uint256 i = 0; i < lenght; i++) {
			_whitelist[i] = whitelist.at(i);
		}
	}

	function pause() public onlyOwner {
		_pause();
		emit DropPaused(block.timestamp);
	}

	function unpause() public onlyOwner {
		_unpause();
		emit DropUnpaused(block.timestamp);
	}

	function withdrawToken(address token, uint256 amount) public onlyOwner {
		safeTokenTransfer(token, payable(_msgSender()), amount);
		emit WithdrawToken(token, amount);
	}

	function setBaseURI(string memory uri) public onlyOwner {
		require(bytes(uri).length > 0, "BD: invalid uri");
		baseURI = uri;
		emit BaseURIChanged(uri);
	}

	function setFeeMint(uint256 fee) public onlyOwner {
		feeMint = fee;
	}

	function safeMint() public payable whenNotPaused {
		require(msg.value >= feeMint, "BD: Insufficient fee");
		require(tokenIdCounter.current() < maxSupply, "BD: Sold out");

		payable(feeRecipient).transfer(feeMint);
		uint256 diff = msg.value - feeMint;
		if (diff > 0) payable(_msgSender()).transfer(diff);

		tokenIdCounter.increment();
		uint256 tokenId = tokenIdCounter.current();
		_safeMint(_msgSender(), tokenId);

		if (!whitelist.contains(_msgSender())) {
			whitelist.add(_msgSender());
		}
		creator[tokenId] = _msgSender();

		emit Minted(_msgSender(), tokenId, block.timestamp);
	}

	function _baseURI() internal view override returns (string memory) {
		return baseURI;
	}
}
